    function filter_items(){
        if(f_filters.length>0 || c_filters.length>0 || filters.length >0){
            $('#clearFilters').addClass('visible');
        }
        let filter_items = '';
        if(c_filters.length===0 && filters.length===0){
            filter_items = f_filters.join(', ');
            $('.isotope').isotope({
                filter: filter_items
            });
        } else if(f_filters.length===0 && filters.length===0) {
            filter_items = c_filters.join(', ');
            $('.isotope').isotope({
                filter: filter_items
            });
        }else if(f_filters.length===0 && c_filters.length===0) {
            filter_items = filters.join(', ');
            $('.isotope').isotope({
                filter: filter_items
            });
        }else if(f_filters.length===0){
            for(let i=0; i<c_filters.length; i++){
                for(let j=0; j<filters.length; j++){
                    filter_items+=filters[j]+c_filters[i]+", ";
                }
            }
            filter_items=filter_items.substring(0,filter_items.length-2);
            $('.isotope').isotope({
                filter: filter_items
            });
        }else if(c_filters.length===0){
            for(let i=0; i<f_filters.length; i++){
                for(let j=0; j<filters.length; j++){
                    filter_items+=filters[j]+f_filters[i]+", ";
                }
            }
            filter_items=filter_items.substring(0,filter_items.length-2);
            $('.isotope').isotope({
                filter: filter_items
            });
        }else if(filters.length===0){
            for(let i=0; i<f_filters.length; i++){
                for(let j=0; j<c_filters.length; j++){
                    filter_items+=c_filters[j]+f_filters[i]+", ";
                }
            }
            filter_items=filter_items.substring(0,filter_items.length-2);
            $('.isotope').isotope({
                filter: filter_items
            });
        }else {
            for(let i=0; i<f_filters.length; i++){
                for(let j=0; j<c_filters.length; j++){
                    for(let k=0; k<filters.length; k++){
                        filter_items+=c_filters[j]+f_filters[i]+filters[k]+", ";
                    }
                }
            }
            filter_items=filter_items.substring(0,filter_items.length-2);
            $('.isotope').isotope({
                filter: filter_items
            });
        }
    }

